import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
    const [activeTab, setActiveTab] = useState('paste');
    const [code, setCode] = useState('');
    const [file, setFile] = useState(null);
    const [language, setLanguage] = useState('python');
    const [encrypt, setEncrypt] = useState(false);
    const [algorithm, setAlgorithm] = useState('');
    const [algorithms, setAlgorithms] = useState([]);
    const [hashAlgorithm, setHashAlgorithm] = useState('sha256');
    const [hashAlgorithms, setHashAlgorithms] = useState([]);
    const [computedHash, setComputedHash] = useState(null);
    const [verifyHash, setVerifyHash] = useState('');
    const [hashVerificationResult, setHashVerificationResult] = useState(null);
    const [vulnerabilities, setVulnerabilities] = useState([]);
    const [encryptionResult, setEncryptionResult] = useState(null);
    const [fixed, setFixed] = useState(false);
    const [fixedCode, setFixedCode] = useState(null);
    const [fixesApplied, setFixesApplied] = useState([]);
    const [error, setError] = useState(null);
    const [supportedLanguages, setSupportedLanguages] = useState([]);

    useEffect(() => {
        // Fetch supported languages
        axios.get('http://localhost:5000/api/supported-languages')
            .then(response => {
                setSupportedLanguages(response.data.languages);
            })
            .catch(err => {
                console.error('Error fetching languages:', err);
                setError('Failed to fetch supported languages');
            });

        // Fetch encryption algorithms
        axios.get('http://localhost:5000/api/encryption-algorithms')
            .then(response => {
                setAlgorithms(response.data.algorithms);
                setAlgorithm('');
            })
            .catch(err => {
                console.error('Error fetching algorithms:', err);
                setError('Failed to fetch encryption algorithms');
            });

        // Fetch supported hash algorithms
        axios.get('http://localhost:5000/api/supported-hash-algorithms')
            .then(response => {
                setHashAlgorithms(response.data.hash_algorithms);
                setHashAlgorithm(response.data.hash_algorithms[0] || 'sha256');
            })
            .catch(err => {
                console.error('Error fetching hash algorithms:', err);
                setError('Failed to fetch hash algorithms');
            });
    }, []);

    const handleCodeSubmit = (e) => {
        e.preventDefault();
        setError(null);
        setVulnerabilities([]);
        setEncryptionResult(null);
        setFixed(false);
        setFixedCode(null);
        setFixesApplied([]);

        const data = {
            code,
            language,
            encrypt,
            algorithm: encrypt && algorithm ? algorithm : null
        };

        axios.post('http://localhost:5000/api/analyze', data)
            .then(response => {
                if (response.data.success) {
                    setVulnerabilities(response.data.vulnerabilities);
                    setEncryptionResult(response.data.encryption);
                    setFixed(response.data.fixed);
                    setFixedCode(response.data.fixed_code);
                    setFixesApplied(response.data.fixes_applied);
                } else {
                    setError(response.data.error || 'Analysis failed');
                }
            })
            .catch(err => {
                console.error('Error analyzing code:', err);
                setError('Failed to analyze code');
            });
    };

    const handleFileSubmit = (e) => {
        e.preventDefault();
        setError(null);
        setVulnerabilities([]);
        setEncryptionResult(null);
        setFixed(false);
        setFixedCode(null);
        setFixesApplied([]);

        if (!file) {
            setError('Please select a file to upload');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('encrypt', encrypt.toString());
        if (encrypt && algorithm) {
            formData.append('algorithm', algorithm);
        }

        axios.post('http://localhost:5000/api/analyze-file', formData)
            .then(response => {
                if (response.data.success) {
                    setVulnerabilities(response.data.vulnerabilities);
                    setEncryptionResult(response.data.encryption);
                    setFixed(response.data.fixed);
                    setFixedCode(response.data.fixed_code);
                    setFixesApplied(response.data.fixes_applied);
                } else {
                    setError(response.data.error || 'Analysis failed');
                }
            })
            .catch(err => {
                console.error('Error analyzing file:', err);
                setError('Failed to analyze file');
            });
    };

    const handleComputeHash = (e) => {
        e.preventDefault();
        setError(null);
        setComputedHash(null);
        setHashVerificationResult(null);

        const formData = new FormData();
        if (activeTab === 'paste' && code) {
            formData.append('code', code);
        } else if (activeTab === 'upload' && file) {
            formData.append('file', file);
        } else if (activeTab === 'integrity') {
            if (code) {
                formData.append('code', code);
            } else if (file) {
                formData.append('file', file);
            } else {
                setError('Please provide code or a file to compute the hash');
                return;
            }
        } else {
            setError('Please provide code or a file to compute the hash');
            return;
        }
        formData.append('hash_algorithm', hashAlgorithm);

        axios.post('http://localhost:5000/api/compute-hash', formData)
            .then(response => {
                if (response.data.success) {
                    setComputedHash({
                        hash: response.data.hash,
                        hash_algorithm: response.data.hash_algorithm
                    });
                } else {
                    setError(response.data.error || 'Hash computation failed');
                }
            })
            .catch(err => {
                console.error('Error computing hash:', err);
                setError('Failed to compute hash');
            });
    };

    const handleVerifyHash = (e) => {
        e.preventDefault();
        setError(null);
        setHashVerificationResult(null);

        if (!verifyHash) {
            setError('Please provide a hash to verify');
            return;
        }

        const formData = new FormData();
        if (activeTab === 'paste' && code) {
            formData.append('code', code);
        } else if (activeTab === 'upload' && file) {
            formData.append('file', file);
        } else if (activeTab === 'integrity') {
            if (code) {
                formData.append('code', code);
            } else if (file) {
                formData.append('file', file);
            } else {
                setError('Please provide code or a file to verify the hash');
                return;
            }
        } else {
            setError('Please provide code or a file to verify the hash');
            return;
        }
        formData.append('hash', verifyHash);
        formData.append('hash_algorithm', hashAlgorithm);

        axios.post('http://localhost:5000/api/verify-hash', formData)
            .then(response => {
                if (response.data.success) {
                    setHashVerificationResult({
                        is_valid: response.data.is_valid,
                        hash_algorithm: response.data.hash_algorithm
                    });
                } else {
                    setError(response.data.error || 'Hash verification failed');
                }
            })
            .catch(err => {
                console.error('Error verifying hash:', err);
                setError('Failed to verify hash');
            });
    };

    const handleVerifyEncryptionHash = () => {
        if (!encryptionResult || !encryptionResult.encrypted_file || !encryptionResult.integrity_hash) {
            setError('No encryption result available to verify');
            return;
        }

        const formData = new FormData();
        formData.append('hash', encryptionResult.integrity_hash);
        formData.append('hash_algorithm', 'sha256'); // Hardcoded since encrypt_file uses SHA-256

        // Create a temporary file for the encrypted file path
        const encryptedFilePath = encryptionResult.encrypted_file;
        const fileBlob = new Blob([]); // Placeholder blob since we can't upload a file path directly
        const fileObj = new File([fileBlob], 'encrypted_file', { type: 'application/octet-stream' });

        // We can't directly upload a file path from the server, so we need to compute the hash again
        // Alternatively, we can verify on the server by passing the file path, but we'll need a new endpoint
        // For simplicity, let's compute the hash of the encrypted file again and compare
        axios.post('http://localhost:5000/api/compute-hash', {
            code: '', // Not using code
            hash_algorithm: 'sha256'
        }, {
            headers: { 'Content-Type': 'multipart/form-data' },
            // We can't directly send the file path; we need the file content
            // Since we can't access the file content from the browser, let's add a new endpoint
        })
            .then(response => {
                // This approach won't work directly due to file access restrictions
                // Let's add a new endpoint to verify the encryption hash on the server
                setError('This feature requires a server-side verification endpoint');
            })
            .catch(err => {
                console.error('Error verifying encryption hash:', err);
                setError('Failed to verify encryption hash');
            });

        // Placeholder: We'll implement server-side verification in Step 3
    };

    const handleDownload = (filePath, fileType) => {
        const url = `http://localhost:5000/api/download/${fileType}/${encodeURIComponent(filePath)}`;
        const link = document.createElement('a');
        link.href = url;
        link.download = fileType === 'encrypted' ? 'encrypted_file' : 'key_file';
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const handleDownloadFixed = () => {
        axios.post('http://localhost:5000/api/download-fixed', {
            code: fixedCode,
            language
        }, { responseType: 'blob' })
            .then(response => {
                const url = window.URL.createObjectURL(new Blob([response.data]));
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', `fixed_code.${language}`);
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
                window.URL.revokeObjectURL(url);
            })
            .catch(err => {
                console.error('Error downloading fixed code:', err);
                setError('Failed to download fixed code');
            });
    };

    const getSeverityClass = (severity) => {
        switch (severity.toLowerCase()) {
            case 'high':
                return 'bg-danger';
            case 'medium':
                return 'bg-warning';
            case 'low':
                return 'bg-info';
            default:
                return 'bg-secondary';
        }
    };

    const capitalizeAlgorithm = (alg) => {
        return alg
            .split('-')
            .map(word => word.toUpperCase())
            .join('-');
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-200 flex flex-col items-center p-6">
            <h1 className="text-4xl font-extrabold mb-8 text-indigo-800 tracking-tight">
                Code Security Analyzer
            </h1>

            {/* Tabs */}
            <div className="w-full max-w-2xl bg-white rounded-xl shadow-lg overflow-hidden">
                <div className="flex border-b border-gray-200">
                    <button
                        className={`flex-1 py-4 text-lg font-semibold transition-colors duration-200 ${
                            activeTab === 'paste'
                                ? 'bg-indigo-600 text-white border-b-4 border-indigo-800'
                                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                        }`}
                        onClick={() => setActiveTab('paste')}
                    >
                        Paste Code
                    </button>
                    <button
                        className={`flex-1 py-4 text-lg font-semibold transition-colors duration-200 ${
                            activeTab === 'upload'
                                ? 'bg-indigo-600 text-white border-b-4 border-indigo-800'
                                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                        }`}
                        onClick={() => setActiveTab('upload')}
                    >
                        Upload File
                    </button>
                    <button
                        className={`flex-1 py-4 text-lg font-semibold transition-colors duration-200 ${
                            activeTab === 'integrity'
                                ? 'bg-indigo-600 text-white border-b-4 border-indigo-800'
                                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                        }`}
                        onClick={() => setActiveTab('integrity')}
                    >
                        Integrity Check
                    </button>
                </div>

                {/* Tab Content */}
                <div className="p-6">
                    {activeTab === 'paste' && (
                        <form onSubmit={handleCodeSubmit} className="space-y-4">
                            <textarea
                                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 resize-y text-gray-800 form-control"
                                rows="6"
                                placeholder="Paste your code here..."
                                value={code}
                                onChange={(e) => setCode(e.target.value)}
                            />
                            <div className="flex flex-col sm:flex-row sm:space-x-4 space-y-4 sm:space-y-0">
                                <select
                                    className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 flex-1"
                                    value={language}
                                    onChange={(e) => setLanguage(e.target.value)}
                                >
                                    {supportedLanguages.map(lang => (
                                        <option key={lang} value={lang}>{lang}</option>
                                    ))}
                                </select>
                                <label className="flex items-center space-x-2">
                                    <input
                                        type="checkbox"
                                        checked={encrypt}
                                        onChange={(e) => setEncrypt(e.target.checked)}
                                        className="form-checkbox h-5 w-5 text-indigo-600"
                                    />
                                    <span className="text-gray-700">Encrypt Code</span>
                                </label>
                                {encrypt && (
                                    <select
                                        className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 flex-1"
                                        value={algorithm}
                                        onChange={(e) => setAlgorithm(e.target.value)}
                                    >
                                        <option value="">Select Best Algorithm</option>
                                        {algorithms.map(alg => (
                                            <option key={alg} value={alg}>
                                                {capitalizeAlgorithm(alg)}
                                            </option>
                                        ))}
                                    </select>
                                )}
                            </div>
                            <div className="flex flex-col sm:flex-row sm:space-x-4 space-y-4 sm:space-y-0">
                                <select
                                    className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 flex-1"
                                    value={hashAlgorithm}
                                    onChange={(e) => setHashAlgorithm(e.target.value)}
                                >
                                    {hashAlgorithms.map(alg => (
                                        <option key={alg} value={alg}>
                                            {alg.toUpperCase()}
                                        </option>
                                    ))}
                                </select>
                                <button
                                    type="button"
                                    onClick={handleComputeHash}
                                    className="w-full sm:w-auto bg-teal-600 text-white p-3 rounded-lg hover:bg-teal-700 transition duration-200 font-medium"
                                >
                                    Compute Hash
                                </button>
                            </div>
                            <button
                                type="submit"
                                className="w-full bg-indigo-600 text-white p-3 rounded-lg hover:bg-indigo-700 transition duration-200 font-medium"
                            >
                                Analyze Code
                            </button>
                        </form>
                    )}

                    {activeTab === 'upload' && (
                        <form onSubmit={handleFileSubmit} className="space-y-4">
                            <input
                                type="file"
                                onChange={(e) => setFile(e.target.files[0])}
                                className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 text-gray-800"
                            />
                            <div className="flex flex-col sm:flex-row sm:space-x-4 space-y-4 sm:space-y-0">
                                <label className="flex items-center space-x-2">
                                    <input
                                        type="checkbox"
                                        checked={encrypt}
                                        onChange={(e) => setEncrypt(e.target.checked)}
                                        className="form-checkbox h-5 w-5 text-indigo-600"
                                    />
                                    <span className="text-gray-700">Encrypt File</span>
                                </label>
                                {encrypt && (
                                    <select
                                        className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 flex-1"
                                        value={algorithm}
                                        onChange={(e) => setAlgorithm(e.target.value)}
                                    >
                                        <option value="">Select Best Algorithm</option>
                                        {algorithms.map(alg => (
                                            <option key={alg} value={alg}>
                                                {capitalizeAlgorithm(alg)}
                                            </option>
                                        ))}
                                    </select>
                                )}
                            </div>
                            <div className="flex flex-col sm:flex-row sm:space-x-4 space-y-4 sm:space-y-0">
                                <select
                                    className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 flex-1"
                                    value={hashAlgorithm}
                                    onChange={(e) => setHashAlgorithm(e.target.value)}
                                >
                                    {hashAlgorithms.map(alg => (
                                        <option key={alg} value={alg}>
                                            {alg.toUpperCase()}
                                        </option>
                                    ))}
                                </select>
                                <button
                                    type="button"
                                    onClick={handleComputeHash}
                                    className="w-full sm:w-auto bg-teal-600 text-white p-3 rounded-lg hover:bg-teal-700 transition duration-200 font-medium"
                                >
                                    Compute Hash
                                </button>
                            </div>
                            <button
                                type="submit"
                                className="w-full bg-indigo-600 text-white p-3 rounded-lg hover:bg-indigo-700 transition duration-200 font-medium"
                            >
                                Analyze File
                            </button>
                        </form>
                    )}

                    {activeTab === 'integrity' && (
                        <div className="space-y-4">
                            {/* Compute Hash Section */}
                            <div className="space-y-4">
                                <h3 className="text-lg font-semibold text-indigo-800">Compute Hash</h3>
                                <textarea
                                    className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 resize-y text-gray-800 form-control"
                                    rows="6"
                                    placeholder="Paste your code here (optional)..."
                                    value={code}
                                    onChange={(e) => setCode(e.target.value)}
                                />
                                <input
                                    type="file"
                                    onChange={(e) => setFile(e.target.files[0])}
                                    className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 text-gray-800"
                                />
                                <div className="flex flex-col sm:flex-row sm:space-x-4 space-y-4 sm:space-y-0">
                                    <select
                                        className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 flex-1"
                                        value={hashAlgorithm}
                                        onChange={(e) => setHashAlgorithm(e.target.value)}
                                    >
                                        {hashAlgorithms.map(alg => (
                                            <option key={alg} value={alg}>
                                                {alg.toUpperCase()}
                                            </option>
                                        ))}
                                    </select>
                                    <button
                                        onClick={handleComputeHash}
                                        className="w-full sm:w-auto bg-teal-600 text-white p-3 rounded-lg hover:bg-teal-700 transition duration-200 font-medium"
                                    >
                                        Compute Hash
                                    </button>
                                </div>
                            </div>

                            {/* Verify Hash Section */}
                            <div className="space-y-4">
                                <h3 className="text-lg font-semibold text-indigo-800">Verify Hash</h3>
                                <input
                                    type="text"
                                    className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 text-gray-800"
                                    placeholder="Enter hash to verify..."
                                    value={verifyHash}
                                    onChange={(e) => setVerifyHash(e.target.value)}
                                />
                                <div className="flex flex-col sm:flex-row sm:space-x-4 space-y-4 sm:space-y-0">
                                    <select
                                        className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 flex-1"
                                        value={hashAlgorithm}
                                        onChange={(e) => setHashAlgorithm(e.target.value)}
                                    >
                                        {hashAlgorithms.map(alg => (
                                            <option key={alg} value={alg}>
                                                {alg.toUpperCase()}
                                            </option>
                                        ))}
                                    </select>
                                    <button
                                        onClick={handleVerifyHash}
                                        className="w-full sm:w-auto bg-teal-600 text-white p-3 rounded-lg hover:bg-teal-700 transition duration-200 font-medium"
                                    >
                                        Verify Hash
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>

            {/* Results */}
            <div className="w-full max-w-2xl mt-8">
                {error && (
                    <div className="mb-6 p-4 bg-red-100 text-red-700 rounded-lg shadow-md">
                        <p>{error}</p>
                    </div>
                )}

                {computedHash && (
                    <div className="mb-8 p-6 bg-white rounded-xl shadow-lg">
                        <h2 className="text-2xl font-semibold mb-4 text-indigo-800">Computed Hash</h2>
                        <p><strong>Algorithm:</strong> {computedHash.hash_algorithm.toUpperCase()}</p>
                        <p><strong>Hash:</strong> <pre className="break-all">{computedHash.hash}</pre></p>
                    </div>
                )}

                {hashVerificationResult && (
                    <div className="mb-8 p-6 bg-white rounded-xl shadow-lg">
                        <h2 className="text-2xl font-semibold mb-4 text-indigo-800">Hash Verification Result</h2>
                        <p><strong>Algorithm:</strong> {hashVerificationResult.hash_algorithm.toUpperCase()}</p>
                        <p><strong>Result:</strong> {hashVerificationResult.is_valid ? 'Hash matches' : 'Hash does not match'}</p>
                    </div>
                )}

                {vulnerabilities.length > 0 && (
                    <div className="mb-8 p-6 bg-white rounded-xl shadow-lg">
                        <h2 className="text-2xl font-semibold mb-4 text-indigo-800">Vulnerabilities Found</h2>
                        <ul className="space-y-4">
                            {vulnerabilities.map((vuln, index) => (
                                <li
                                    key={index}
                                    className={`p-4 rounded-lg vulnerability-card ${getSeverityClass(vuln.severity)}`}
                                >
                                    <div className="card-header">{vuln.severity.toUpperCase()} Severity</div>
                                    <div className="mt-2">
                                        <p><strong>Type:</strong> {vuln.type}</p>
                                        <p><strong>File:</strong> {vuln.file_path}</p>
                                        <p><strong>Line:</strong> {vuln.line_number}</p>
                                        <p><strong>Code Snippet:</strong> <pre>{vuln.code_snippet}</pre></p>
                                        <p><strong>Recommendation:</strong> {vuln.recommendation}</p>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {fixed && fixedCode && (
                    <div className="mb-8 p-6 bg-white rounded-xl shadow-lg">
                        <h2 className="text-2xl font-semibold mb-4 text-indigo-800">Fixed Code</h2>
                        <p className="mb-2 text-gray-700">The following vulnerabilities were fixed:</p>
                        <ul className="space-y-4 mb-4">
                            {fixesApplied.map((fix, index) => (
                                <li key={index} className="p-4 bg-gray-50 rounded-lg border border-gray-200">
                                    <p><strong>Type:</strong> {fix.type}</p>
                                    <p><strong>Line:</strong> {fix.line_number}</p>
                                    <p><strong>Original:</strong> <pre>{fix.original}</pre></p>
                                    <p><strong>Fixed:</strong> <pre>{fix.fixed}</pre></p>
                                </li>
                            ))}
                        </ul>
                        <textarea
                            className="w-full p-3 border rounded-lg bg-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500 resize-y text-gray-800 form-control"
                            rows="6"
                            value={fixedCode}
                            readOnly
                        />
                        <button
                            onClick={handleDownloadFixed}
                            className="mt-4 bg-indigo-600 text-white p-2 rounded-lg hover:bg-indigo-700 transition duration-200 font-medium"
                        >
                            Download Fixed Code
                        </button>
                    </div>
                )}

                {encryptionResult && encryptionResult.success && (
                    <div className="p-6 bg-white rounded-xl shadow-lg">
                        <h2 className="text-2xl font-semibold mb-4 text-indigo-800">Encryption Results</h2>
                        <p><strong>Algorithm Used:</strong> {encryptionResult.algorithm}</p>
                        <p><strong>Original Size:</strong> {encryptionResult.original_size} bytes</p>
                        <p><strong>Encrypted Size:</strong> {encryptionResult.encrypted_size} bytes</p>
                        <p><strong>Encryption Time:</strong> {encryptionResult.encryption_time.toFixed(3)} seconds</p>
                        <p><strong>Integrity Hash:</strong> <pre className="break-all">{encryptionResult.integrity_hash}</pre></p>
                        <div className="mt-4 flex space-x-4">
                            <button
                                onClick={() => handleDownload(encryptionResult.encrypted_file, 'encrypted')}
                                className="bg-green-600 text-white p-2 rounded-lg hover:bg-green-700 transition duration-200 font-medium"
                            >
                                Download Encrypted File
                            </button>
                            <button
                                onClick={() => handleDownload(encryptionResult.key_file, 'key')}
                                className="bg-green-600 text-white p-2 rounded-lg hover:bg-green-700 transition duration-200 font-medium"
                            >
                                Download Key File
                            </button>
                            <button
                                onClick={handleVerifyEncryptionHash}
                                className="bg-teal-600 text-white p-2 rounded-lg hover:bg-teal-700 transition duration-200 font-medium"
                            >
                                Verify Integrity Hash
                            </button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default App;
