import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Alert, Card, Nav, Tab } from 'react-bootstrap';
import { Light as SyntaxHighlighter } from 'react-syntax-highlighter';
import { docco } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [code, setCode] = useState('');
  const [file, setFile] = useState(null);
  const [language, setLanguage] = useState('python');
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('code');

  const handleCodeChange = (e) => {
    setCode(e.target.value);
  };

  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      const selectedFile = e.target.files[0];
      setFile(selectedFile);
      
      // Detect language from file extension
      const fileExtension = selectedFile.name.split('.').pop().toLowerCase();
      if (fileExtension === 'py') setLanguage('python');
      else if (fileExtension === 'js') setLanguage('javascript');
      else if (fileExtension === 'java') setLanguage('java');
    }
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  const analyzeCode = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await axios.post('/api/analyze', {
        code,
        language
      });
      
      setResults(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to analyze code');
    } finally {
      setLoading(false);
    }
  };

  const analyzeFile = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }
    
    setLoading(true);
    setError(null);
    
    const formData = new FormData();
    formData.append('file', file);
    
    try {
      const response = await axios.post('/api/analyze-file', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      
      setResults(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to analyze file');
    } finally {
      setLoading(false);
    }
  };

  const renderResults = () => {
    if (!results) return null;
    
    return (
      <div className="mt-4">
        <h3>Analysis Results</h3>
        {results.vulnerabilities && results.vulnerabilities.length === 0 ? (
          <Alert variant="success">No vulnerabilities found!</Alert>
        ) : (
          <div>
            {results.vulnerabilities && results.vulnerabilities.map((vuln, index) => (
              <Card key={index} className="mb-3 vulnerability-card">
                <Card.Header className={`bg-${getSeverityColor(vuln.severity)}`}>
                  <strong>{vuln.type}</strong> - Severity: {vuln.severity}
                </Card.Header>
                <Card.Body>
                  <Card.Title>{vuln.description}</Card.Title>
                  <Card.Text>
                    <strong>Location:</strong> Line {vuln.line_number}<br />
                    <strong>Recommendation:</strong> {vuln.recommendation}
                  </Card.Text>
                  {vuln.code_snippet && (
                    <SyntaxHighlighter language={language} style={docco} wrapLines={true} showLineNumbers={true} startingLineNumber={vuln.line_number}>
                      {vuln.code_snippet}
                    </SyntaxHighlighter>
                  )}
                </Card.Body>
              </Card>
            ))}
          </div>
        )}
      </div>
    );
  };

  const getSeverityColor = (severity) => {
    switch (severity.toLowerCase()) {
      case 'high':
        return 'danger';
      case 'medium':
        return 'warning';
      case 'low':
        return 'info';
      default:
        return 'secondary';
    }
  };

  return (
    <Container className="mt-5 mb-5">
      <h1 className="text-center mb-4">Secure Code Analyzer</h1>
      
      <Tab.Container activeKey={activeTab} onSelect={k => setActiveTab(k)}>
        <Nav variant="tabs" className="mb-3">
          <Nav.Item>
            <Nav.Link eventKey="code">Code Input</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="file">File Upload</Nav.Link>
          </Nav.Item>
        </Nav>
        
        <Tab.Content>
          <Tab.Pane eventKey="code">
            <Row>
              <Col md={12}>
                <Form.Group className="mb-3">
                  <Form.Label>Enter code to analyze:</Form.Label>
                  <Form.Control
                    as="textarea"
                    rows={10}
                    value={code}
                    onChange={handleCodeChange}
                    placeholder="Paste your code here..."
                  />
                </Form.Group>
                
                <Form.Group className="mb-3">
                  <Form.Label>Language:</Form.Label>
                  <Form.Select value={language} onChange={handleLanguageChange}>
                    <option value="python">Python</option>
                    <option value="javascript">JavaScript</option>
                    <option value="java">Java</option>
                  </Form.Select>
                </Form.Group>
                
                <Button 
                  variant="primary" 
                  onClick={analyzeCode} 
                  disabled={loading || !code}
                >
                  {loading ? 'Analyzing...' : 'Analyze Code'}
                </Button>
              </Col>
            </Row>
          </Tab.Pane>
          
          <Tab.Pane eventKey="file">
            <Row>
              <Col md={12}>
                <Form.Group className="mb-3">
                  <Form.Label>Upload a file to analyze:</Form.Label>
                  <Form.Control
                    type="file"
                    onChange={handleFileChange}
                  />
                </Form.Group>
                
                <Button 
                  variant="primary" 
                  onClick={analyzeFile} 
                  disabled={loading || !file}
                >
                  {loading ? 'Analyzing...' : 'Analyze File'}
                </Button>
              </Col>
            </Row>
          </Tab.Pane>
        </Tab.Content>
      </Tab.Container>

      {error && <Alert variant="danger" className="mt-3">{error}</Alert>}
      
      {renderResults()}
    </Container>
  );
}

export default App;
